### 附带详细注释的代码

```cpp
#include <bits/stdc++.h>
using namespace std;

inline int gin(){//快读
	char c=getchar();
	int s=0,f=1;
	while(c<'0'||c>'9'){
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9'){
		s=(s<<3)+(s<<1)+(c^48);
		c=getchar();
	}
	return s*f;
}

const int N=5e3+5;
int n,m,cnt[N],d[N],tot=0,head[N];
//cnt记录入队次数
bool h[N],t;
//t记录是否为负环
queue<int> q;

struct edge{//用结构体存储链接表
	int dis,ne,to;
}e[N<<1];

inline void add(int u,int v,int w){//连边
	e[++tot].dis=w;
	e[tot].to=v;
	e[tot].ne=head[u];
	head[u]=tot;
}

inline void spfa(){//最短路
	memset(d,63,sizeof d);//初始化
	h[0]=1,t=0,d[0]=0;
	q.push(0);
	while(q.size()){
		int u=q.front();q.pop();h[u]=0;
		if(cnt[u]==n-1){t=1;return;}//如果已经入队了n-1次就出现负环了
		cnt[u]++;
		for(int i=head[u];i;i=e[i].ne){
			int v=e[i].to,w=e[i].dis;
			if(d[v]>d[u]+w){
				d[v]=d[u]+w;
				if(!h[v])h[v]=1,q.push(v);
			}
		}
	} 
}

int main(){
	n=gin(),m=gin();
	for(int i=1;i<=m;i++){
		int u=gin(),v=gin(),w=gin();
		add(v,u,w);
	}
	for(int i=n;i>=1;i--)add(0,i,0);//之前正序被卡，感谢题解区大佬解释
	spfa();
	if(t){printf("NO");return 0;}
	for(int i=1;i<=n;i++)printf("%d ",d[i]);
	return 0;
}

