我是分割线
```cpp
#include<bits/stdc++.h>
using namespace std;
const int N=1000;
const int mod=1000000007;
int n,m;
int id(int i,int j){ i行j
    return (i-1)*m+j;
}
char s[N+5][N+5];
bool vis[N*N+5]; 
int clr[N+5][N+5],cn[N+5],head[N*N+5],fa[N*N+5];
int cnt;
struct edge{
    int next,des;
}e[N*N+5];
void add(int a,int b){//添加边
	cnt++;
	e[cnt].des=b;
	e[cnt].next=head[a];
	head[a]=cnt;
}
long long dp(int u){//dp函数，代表2元组
	long long cur=1;vis[u]=1;
	for(int i=head[u];i;i=e[i].next){
		int v=e[i].des;cur=cur*dp(v)%mod;
	}
	return (cur+1)%mod;
}
struct graph{//结构体
	int cnt;
    vector<int>link[3*N+5];
    int f[3*N+5];
    queue<int>q;
    void reset(){
        cnt=0;
        for(int i=1;i<=3*m;i++)link[i].resize(0);
        memset(f,0,sizeof(f));
    }
    void add(int a,int b){
        link[a].push_back(b);
        link[b].push_back(a);
    }
    bool bfs(int s){//广搜
        bool flag=0;
        q.push(s);
        while(!q.empty()){
            int u=q.front();
            q.pop();
            for(int i=0;i<link[u].size();i++){
                int v=link[u][i];
                if(!f[v]){
                    if(v<=m)flag=1;
                    f[v]=f[u];
                    q.push(v);
                }
            }
        }
        return flag;
    }
    inline void calc(int i){//计算
        reset();
        for(int j=1;j<m;j++){
            if(s[i][j]=='.'&&s[i][j+1]=='.')add(id(1,j),id(1,j+1));
            if(s[i+1][j]=='.'&&s[i+1][j+1]=='.')add(id(2,j),id(2,j+1));
            if(s[i][j]=='.'&&s[i+1][j]=='.')add(id(1,j),id(2,j));
            if(s[i+1][j]=='.')add(id(2,j),id(3,clr[i+1][j]));
        } 
        for(int j=1;j<=cn[i+1];j++){
            if(f[id(3,j)])continue;
            f[id(3,j)]=++cn[i];
            if(!bfs(id(3,j)))cn[i]--; 
        }
        for(int j=1;j<m;j++){
			if(f[id(1,j)]){
				clr[i][j]=f[id(1,j)];
			} 
		} 
    }
};
graph G;
int main(){
	cin>>n>>m;
	bool flag=1;
	for(int i=1;i<=n;i++){//输入每行
		scanf("%s",s[i]+1);
	}
	for(int i=n-1;i>=2;i--){
		G.calc(i);
		for(int j=2;j<m;j++){
			if(s[i][j]=='.'&&s[i+1][j]=='.')
            fa[id(i+1,clr[i+1][j])]=id(i,clr[i][j]);
		}
		for(int j=1;j<=cn[i+1];j++)
            if(fa[id(i+1,j)])add(fa[id(i+1,j)],id(i+1,j));
        for(int j=2;j<m;j++)if(s[i][j]=='.'&&!clr[i][j]){
            if(s[i][j-1]=='.')clr[i][j]=clr[i][j-1];
            else clr[i][j]=++cn[i];
        }
	}
	long long ans=1;
	for(int i=2;i<n;i++){
		for(int j=2;j<n;j++){
			if(s[i][j]=='.'&&!vis[id(i,clr[i][j])])ans=ans*dp(id(i,clr[i][j]))%mod;
		}
	}//输出
	cout<<ans<<endl;
	return 0;
} 
```

