		}
}
void solve()
{
	for(int i=1,j=0;i<=n;i++)
	{
		del(val[i-1]);
		while(j<n&&sum<k)ins(val[++j]);
		if(sum<k)break;
		ans+=n-j+1;
	}
}
int main()
{
	init();
	if(n>5e5)solve();
	else dfs(root,1);
	printf("%lld",ans);
	return 0;
}
