ll jc[N];
ll ans[N];
int main()
{
	int n=20;jc[0]=1;
	F(i,1,n) jc[i]=jc[i-1]*i%p;
	F(t,1,n) F(i,1,t) F(j,1,i) F(k,1,j) ans[t]+=jc[min(i-j,j-k)];
