        flag=false;k=tot=0;ret=1;
        for(int i=0;ch[i]!='\0';i++){
            if(isdigit(ch[i])){
                k*=10;
                k+=ch[i]-'0';//每一个数字用k暂时存储。
                flag=true;
            }
            else if(ch[i]=='-'&&isdigit(ch[i+1])) {
