                tot+=k*ret;
                k=0;
                ret=1;
                }
                else ret=-1;
            }
            else{
                tot+=k*ret;
                k=0;
                ret=1;
            }
            temp=i;
        }//最后一位如果是数字会遗失。
        if(isdigit(ch[temp])) tot+=k*ret;
        if(flag)
        cout<<tot<<endl;
    }
    //system("pause");
    return 0;
}
