int N[10000],K[10000];
int Om=8;
int pri[1919810],tot;
//int lpf[maxn];//最小质因子 
bitset<maxn>v;
int alpha[maxn];//出现几次 
int Beta[maxn];//pri[lpf[i]]^alpha[i]
int ome[maxn];//几个因子 
int G[maxn][9];
int mx;
void shai(int n){
	for(int i=2;i<=n;i++){
		if(!v[i])pri[++tot]=i,ome[i]=1,alpha[i]=1,Beta[i]=i;
		for(int j=1;j<=tot&&i*pri[j]<=n;j++){
			v[i*pri[j]]=1;
			if(i%pri[j]==0){
				alpha[i*pri[j]]=alpha[i]+1;
				Beta[i*pri[j]]=Beta[i]*pri[j];
				ome[i*pri[j]]=ome[i];
				break;
			}
			alpha[i*pri[j]]=1;
			Beta[i*pri[j]]=pri[j];
			ome[i*pri[j]]=ome[i]+1;
		}
	}
	G[1][0]=1;
	for(int i=2;i<=n;i++){
		for(int j=0;j<=ome[i];j++)G[i][j]=G[i/Beta[i]][j];
		for(int j=1;j<=ome[i];j++)G[i][j]=(G[i][j]+1ll*G[i/Beta[i]][j-1]*alpha[i])%p;
	}
	for(int i=0;i<=Om;i++)
	for(int j=1;j<=n;j++)
	G[j][i]=(G[j-1][i]+G[j][i])%p;
}
int main(){
#ifndef ONLINE_JUDGE
	freopen("testdata.in","r",stdin);
#endif
	cin>>T;
	for(int i=0;i<T;i++)cin>>N[i]>>K[i],mx=max(mx,N[i]);
	if(mx<=100)Om=4;
	shai(mx);
	for(int o=0;o<T;o++){
		int ans=0;
		for(int i=0,j=1;i<=Om;i++,j=1ll*j*K[o]%p)
			ans=(ans+1ll*G[N[o]][i]*j)%p;
		cout<<ans<<endl;
	}
#ifndef ONLINE_JUDGE
	cerr<<endl<<(double)clock()/CLOCKS_PER_SEC;
#endif
}
