	int n;cin>>n;
	b[1]=1;
	for(int i=2;i<=n;i++){
		if(i%2==0){
		b[i]=(b[i/2]+b[i-1])%a;
		}else{
			b[i]=b[i-1]%a;
		}
	}
	cout<<b[n]%a<<endl;
	return 0;
}

