#include<bits/stdc++.h>
#define mod 1000000007
#define reg register
#define maxn 405
using namespace std;
int n, m, c, fac[maxn], ifac[maxn], inv[maxn];
int g[maxn][maxn], d[maxn], p[maxn][maxn];
int ksm(int u, int v){
    int s = 1;
    for(int i = v; i; i >>= 1){
        if(i & 1) s = 1ll * s * u % mod;
        u = 1ll * u * u % mod;
    }
    return s;
}
int inc(int x, int y){
    return x + y >= mod ? x + y - mod : x + y;
}
int C(int x, int y){
    return 1ll * fac[x] * ifac[y] % mod * ifac[x - y] % mod;
}
int get(int x, int y, int digit){
    int ans = 0;
    for(int i = digit; i >= 0; i--){
        int hh = ksm(i + 1, x * y); 
        int x = 1ll * C(digit, i) * hh % mod;
        if(digit - i & 1) ans = inc(ans, mod - x);
        else ans = inc(ans, x);
    }
    return ans;
}
int main(){
    cin >> n >> m >> c;
    fac[0] = 1; ifac[0] = 1; inv[0] = 1;
    for(int i = 1; i < maxn; i++){
        fac[i] = 1ll * fac[i - 1] * i % mod;
        inv[i] = (i == 1) ? 1 : 1ll * inv[mod % i] * (mod - mod / i) % mod;
        ifac[i] = 1ll * ifac[i - 1] * inv[i] % mod;
    }
   for(int i = 1; i <= n; i++) 
        for(int j = 1; j <= m; j++)
            p[i][j] = get(i, j, c);
    for(int j = 1; j <= m; j++)
        for(int i = 1; i <= n; i++){
            g[i][j] = p[i][j];
            for(int k = 0; k < i; k++){
                int x = 1ll * C(i, k) * g[k][j] % mod;
                g[i][j] = inc(g[i][j], mod - x);
            }
        }
    for(int j = 1; j <= m; j++){
        d[j] = g[n][j];
        for(int k = 1; k < j; k++){
            int x = 1ll * C(j, k) * d[k] % mod;
            d[j] = inc(d[j], mod - x);
        }
    }
    cout << d[m] << endl;
}
