inline void getpprufer()
{
	int i;
	for(i=1;i<=n-1;i++) d[a[i]]++;
	priority_queue <int> q;
	for(i=1;i<=n-1;i++) if(!d[i]) q.push(-i);
	int cnt=0;
	while(!q.empty())
	{
		if(cnt==n-2) break;
		int t=q.top();
		t=-t;
		q.pop();
		prufer[++cnt]=a[t];
		d[a[t]]--;
		if(!d[a[t]]) q.push(-a[t]);		
