### $Code:$

```cpp
#include <iostream>
#include <cstdio>
#include <algorithm>
#include <cstring>
#include <cmath>
using namespace std;
#define int __int128
const int mod = ((int)1 << 32);
inline int read(){
	int x = 0, f = 1;
	char ch = getchar();
	while(ch < '0' || ch > '9'){
		if(ch == '-') f = -1;
		ch = getchar();
	}
	while(ch >= '0' && ch <= '9'){
		x = (x << 3) + (x << 1) + (ch ^ 48);
		ch = getchar();
	}
	return x * f;
}//快读板板
inline void write(int x){
	if(x < 0) putchar('-'), x = -x;
	if(x / 10 == 0){
		putchar(x % 10 + 48);
		return;
	}
	write(x / 10);
	putchar(x % 10 + 48);
}//快输板板
int n, a[500010], ans, b[32][2];
signed main(){
	n = read();
	for(register int i = 1; i <= n; ++i){
		a[i] = read();
		int x = a[i];
		for(int j = 0; j <= 30; ++j){
			if(x & 1) b[j][1]++;//如果这一位是1就加到1这
			else b[j][0]++;//否则加到0这
			x >>= 1;//每次把这个数向右移一位
		}
	}
	for(register int i = 0; i <= 30; ++i){
		ans += ((b[i][0] * b[i][0] % mod * b[i][1] % mod * b[i][1] % mod * 6 % mod + b[i][0] * b[i][1] % mod * b[i][0] % mod * b[i][0] % mod * 2 % mod) % mod + b[i][1] * b[i][1] % mod * b[i][1] % mod * b[i][0] % mod * 2 % mod) % mod * ((int)1 << i) % mod; //希望大家能看得懂QAQ
		ans %= mod;
	}
	write(ans);//完结撒花-v-
	return 0;
}
