	p_p[++x_p].v=v;
	p_p[x_p].n_t=h_d[u];
	h_d[u]=x_p;
}
void n_w(int x){//换配 
	if(!x)return ;
	n_w(l_k[p_r[x]]);//一直走到头 

	//换配 
	l_k[x]=p_r[x];
	l_k[p_r[x]]=x;
}
int f(int k){//找父节点 
	if(k==f_a[k])return k;
	return f_a[k]=f(f_a[k]);//路径压缩 
}
int l_c(int x,int y){
	x_x++;//开新点（环缩点） 
	while(1){
		x=f(x);//上爬，爬到父节点（如果不是自己的话，相当于直接出环） 
		if(g_h[x]==x_x)return x;
