		else if(x)g_h[x]=x_x;//更新节点标记 
		x=p_r[l_k[x]];//更新为当前节点匹配的节点的来的点
		//下一个可能换配的点，即标记 b_b 为 1 的点 
		swap(x,y);//交换标号，两头一起跑，可以成环相遇
		//如果只有一个点跑就跑回起点了 
	}
}
void s_k(int x,int y,int f_f){
