		p_r[x]=y;//更新记录来的点 
		y=l_k[x];//记录匹配的点 
		f_a[x]=f_a[y]=f_f;//匹配的一对点更新父节点 
		if(b_b[y]==2){//遍历过 
			b_b[y]=1;//标记 
			q_q.push(y);//入队尝试 
		}
		x=p_r[y];//更新到 y 的点 
	}
}
int b_s(int x){
	for(int i=1;i<=n;i++){//初始化信息 
		b_b[i]=0;
		p_r[i]=0;
		f_a[i]=i;//初始化父节点 
	}
	b_b[x]=1;//标记 
	q_q=queue<int>();//清空队列 
