long long list1[10000001];
long long pow (long long a,long long b)
{
	if (b==0)
	return 1;
	long long c=pow(a,b>>1);
	if (b&1)
	return a*c%998244353*c%998244353;
	return c*c%998244353;
}
void Euler (long long n,long long k)
{
	miu[1]=1;
	f[1]=1;
	list1[1]=1;
	int i,j;
	for (i=2;i<=n;i++)
	{
		if (!composite[i])
		{
			PN++;
			prime[PN]=i;
			miu[i]=-1;
			f[i]=1;
			list1[i]=pow(i,k);
