		for (j=1;i*prime[j]<=n&&i%prime[j]>0;j++)
		{
			composite[i*prime[j]]=1;
			if (i%prime[j])
			{
				miu[i*prime[j]]=-miu[i];
				f[i*prime[j]]=f[i];
			}
			else
			{
				miu[i*prime[j]]=0;
				f[i*prime[j]]=0;
			}
			list1[i*prime[j]]=list1[i]*list1[prime[j]]%998244353;
		}
		if (i*prime[j]<=n)
		{
			composite[i*prime[j]]=1;
			if (i%prime[j])
			{
				miu[i*prime[j]]=-miu[i];
				f[i*prime[j]]=f[i];
