				f[i*prime[j]]=0;
			}
			list1[i*prime[j]]=list1[i]*list1[prime[j]]%998244353;
		}
	}
}
int min (int a,int b)
{
	return a<b?a:b;
}
int n,i,j,l;
long long k,list2[5000001],temp,answer;
int main ()
{
