		list2[i]+=(list1[l]*min(l-1,temp-l+1)+list1[l+1]*min(l,temp-l)+list1[l+2]*min(l+1,temp-l-1)+list1[l+3]*min(l+2,temp-l-2))%998244353;
		for (;j<=n<<1;j+=i,++l)
