	cout<<n<<endl;
	for(i=1;i<=n;i++){
		__int128 now=0;
		printf("0");
		for(j=1;j<=n;j++){
			now+=((__int128(1))<<a[j]);
			printf(" ");
			OUT(now);
		}printf("\n");
		for(j=1;j<=n;j++)tmp[j%n+1]=a[j];
		for(j=1;j<=n;j++)a[j]=tmp[j];
