#define Rep(i,j,k) for(int i=(int)(j);i>=(int)(k);i--)
inline ll read(){
	ll x=0;char ch=getchar();bool f=0;
	for(;!isdigit(ch);ch=getchar()) if(ch=='-') f=1;
	for(;isdigit(ch);ch=getchar()) x=x*10+ch-'0';
	return f?-x:x;
}
void write(ull x){
	if(x<0) putchar('-'),x=-x;
	if(x>=10) write(x/10);putchar(x%10+'0');
}
void writeln(ull x){write(x);puts("");}
void writep(ull x){write(x);putchar(' ');}

int const N=1e6+3;
int n,a[N];
ull ans;

int main(){
	n=read();
	for (int i=1;i<=n;i++)
	  a[i]=read();
	int l=1,r;//左右指针 
	a[n+1]=1;//末尾加上一个1，方便之后的处理 
	for (int i=1;i<=n+1;i++)
	  if (a[i]==1){//找到1 
	  	  r=i-1;//框定区间 
	  	  if (l<=r) ans++;//如果区间为1 ... 1那么就要加上1的代价，否则1的代价会在下面的ans+=a[l]被处理 
		  while (l<r){//对区间处理 
		  	  if (a[l]>a[r] || a[l]==a[r] && a[l]*a[l+1]<a[r]*a[r-1]){//从两边往中间取，并取相对大的数 
				  ans+=a[l]*a[l+1];
				  l++;
			  }
			  else{
				  ans+=a[r]*a[r-1];
				  r--;
			  }
		  }
		  ans+=a[l];//加上最后一取的数 
		  l=i+1;//左指针指到下一个区间 
	  }
	writeln(ans-1);//因为我们把最后一个1也算了进去（实际上是不用的，所以要-1） 
	return 0; 
}
```

