#pragma GCC target("avx")
#pragma GCC optimize(3)
#pragma GCC optimize("Ofast")
#include<cstdio>
#define rg register
#define ll long long
ll ans=1;
int n,m,p,t;
bool mp[300000007];
int pr[40000007],cnt;
inline void init()
{
	for(rg int i=2;i<=n;++i)
	{
		if(!mp[i])
		{
			pr[++cnt]=i,(i&1)&&(ans=ans*i%p);
		}
		for(rg int j=1;i*pr[j]<=n;++j)
		{
			mp[i*pr[j]]=1;
			if(i%pr[j]==0)break;
		}
	}
}
inline ll inv(ll x)
{
	ll res=1;
	int v=p-2;
	while(v)
	{
		(v&1)&&(res=res*x%p),x=x*x%p,v>>=1;
	}
	return res;
}
int main()
{
	scanf(" %d %d %d",&n,&m,&p);
	init();
	while(m--)
	{
		scanf(" %d",&t);
		if(t==1)
		{
			ans=-1;break;
		}
		if(pr[t]==0)continue;
		ans=ans*inv(pr[t])%p;pr[t]=0;
	}
	printf("%lld\n",(~ans)?(ans+p-1)%p:-1ll);
	return 0;
}
```
