#include<cstdio>
#include<iostream>
#include<cstring>
#define CT const int&
using std::max;
const int MAXN=2e6+7;
int n,tp,ccnt,top;
int a[MAXN];
int nxt[MAXN],home[MAXN],to[MAXN],v[MAXN],b[MAXN];
int vis[MAXN],dep[MAXN],st[MAXN],res[MAXN];
inline void ct(CT x,CT y) {
	ccnt++;
	nxt[ccnt]=home[x];
	home[x]=ccnt;
	to[ccnt]=y;
}

inline void dfs(int x) {
	res[++top]=x;
	vis[x]++;
	for(int i=home[x]; i; i=nxt[i]) {
		int v=to[i];
		if(!vis[v]) {
			dfs(v);
			dep[x]=max(dep[x],dep[v]+1);
		}
	}
}

inline void solve() {
	st[tp=1]=1;
	for(int i=2; i<=n; ++i) {
		while((tp+1>a[i])) {
			ct(st[tp],i);
			--tp;
		}
		st[++tp]=i;
	}
	int base=1;
	for(int i=1; i<=n; ++i) {
		if(vis[i])continue;
		vis[i]=1;
		top=0;
		dfs(i);
		for(int k=1; k<=top; ++k) {
			int u=res[k];
			b[u]=dep[u]+base;
		}
		base+=top;
	}
	return ;

}

int main() {
	scanf("%d",&n);
	for(int i=1; i<=n; ++i) {
		scanf("%d",&a[i]);
		if(a[i]==-1)a[i]=a[i-1]+1;
	}
	solve();
	for(int i=1; i<=n; ++i)printf("%d ",b[i]);
	return 0;
}

