    }
}
void KMP(char *S, int len1, char *T, int len2) {
    for(int i = 0, j = 0; i <= len1; ) {
        if(j == len2)
            sum[i] = 1;
        if(S[i + 1] == T[j + 1])
            ++i, ++j;
        else {
            if (j == 0) ++i;
            else j = _next[j];
        }
    }
}

int main() {
    scanf("%d %d", &n, &m);
    scanf("%s %s", str1 + 1, str2 + 1);
    Get_Next(str2, m);
    KMP(str1, n, str2, m);
    for(int i = 1; i <= n; ++i) sum[i] += sum[i - 1];
    for(int i = 1; i <= n; ++i) t.extend(str1[i] - 'a' + 1);
    t.refresh();
    t.dfs(1);
    printf("%u", ans);
    return 0;
}
