int dfs( int pos , int lead , int leadnum , int limit , int sum ) {
	if( pos == len + 1 ) return sum >= len - leadnum - sum;
	if( ~dp[ pos ][ sum ][ leadnum ] && !lead && !limit ) return dp[ pos ][ sum ][ leadnum ];

	int Ans = 0 , Mbit = limit ? a[ len - pos + 1 ] : 1;
