```c++
#include <bits/stdc++.h>
using namespace std;
int n,m;
long long sum,ans,a[100005];
int main() {
    scanf("%d%d",&m,&n );
    sum=-m;
    for(int i=0;i<n;i++) {
        scanf( "%d", &a[i] );
        sum += a[i];
    }
    sort(a, a+n);
    for(int i=0;i<n;i++) {
        long long w=min(a[i], sum/(n-i) );
        ans += w*w;
        sum -= w;
    }
    printf( "%lld\n", ans );
    return 0;
}
```
