	int n, m, k;
	scanf("%d%d%d", &n, &m, &k);
	f[1][0][0][0] = 1;
	for (int i(1); i <= n; ++ i) {
		for (int j(0); j <= m; ++ j)
		for (int S(0); S < 1 << k + 1; ++ S) {
			for (int t(min(i - 1, k)); t; -- t) {
				upd(f[i][j][S][t - 1], f[i][j][S][t]);
				if (i > t) upd(f[i][j + 1][S ^ 1 ^ 1 << t][t], f[i][j][S][t]);
			}
			if (!(S & (1 << k))) upd(f[i + 1][j][S << 1][min(i, k)], f[i][j][S][0]);
		}
	}
	printf("%d", f[n][m][0][0]);
	return 0;
}
