map<pair<ull,ull>,int> mp;
vector<int> hav[64];
const int MOD=1e9+7;
inline int dfs(ull x,ull y){
//	cout<<x<<' '<<y<<endl;
	if(mp.count(make_pair(x,y)))	return mp[{x,y}];
	int ans=1;
	for(int i=1;i<(1<<m);i++){
		int bit=((i<32)?(x>>(i<<1)&3):(y>>((i<<1)-64)&3));
		if(bit<=1){
			ull tmpx=x,tmpy=y;
			foreach(it,hav[i]){
				int j=*it;
				if(j<32){
					if((x>>(j<<1)&3)<2)	x+=(1ull<<(j<<1));
				}
				else{
					if((y>>((j-32)<<1)&3)<2)	y+=(1ull<<((j-32)<<1));
				}
			}
			ans=(ans+1ll*dfs(x,y)*cnt[i])%MOD;
			x=tmpx,y=tmpy;
		}
	}
	return mp[{x,y}]=ans;
}
signed main(){
	for(int i=2;i*i<=n;i++){
		if(n%i==0){
			int cnt=0;
			while(n%i==0)	n/=i,cnt++;
			t[++m]=i;
			f[m]=cnt;
		}
	}
	if(n>1)	t[++m]=n,f[m]=1;
	for(int i=0;i<(1<<m);i++){
		cnt[i]=1;
		for(int j=1;j<=m;j++){
			if(i>>(j-1)&1)	cnt[i]*=f[j];
		}
	}
	for(int i=0;i<(1<<m);i++)
		for(int j=0;j<(1<<m);j++)
			if(i&j)
				hav[i].push_back(j);
	int ans=dfs(0,0);
	cout<<(ans-1+MOD)%MOD<<endl;
	return 0;
}
