```cpp
#include <bits/stdc++.h>
using namespace std;
string s[1001];
int sum,vis[1001],viss[1001],ans[20010],ans2[20010];
int n,tot,cnt,a[1001],in[1001],head[200010],anss[20010];

struct node {
	int to,net;
} e[200010];

void add(int u,int v) {
	e[++tot].to=v;
	e[tot].net=head[u];
	head[u]=tot;
}

int topo() {
	queue<int> q;
	for(int i=0;i<=25;i++) {
		if(!in[i]&&vis[i]) q.push(i);
	}
	if(!q.size()) return -1;
	while(!q.empty()) {
		bool flag=false;
		int x=q.front();
		q.pop();
		if(viss[x]) return -1;
		viss[x]=1;
		ans[++sum]=x;
		ans2[sum]=x;
		for(int i=head[x];i;i=e[i].net) {
			int v=e[i].to;
			if(--in[v]==0) {
				flag=true;
				q.push(v);
			}
		}
	}
	return sum;
}


int main() {
	scanf("%d",&n);
	for(int i=1;i<=n;i++) {
		cin>>s[i];
	}
	for(int i=1;i<=n;i++) {
		scanf("%d",&a[i]);
	}
	for(int i=1;i<n;i++) {
		int k=0,kk=0;
		while(k<s[a[i]].size()&&kk<s[a[i+1]].size()) {
			if(s[a[i]][k]!=s[a[i+1]][kk]) {
				add(s[a[i]][k]-'a',s[a[i+1]][kk]-'a');	
				if(!vis[s[a[i]][k]-'a']) cnt++,vis[s[a[i]][k]-'a']=1;
				if(!vis[s[a[i+1]][kk]-'a']) cnt++,vis[s[a[i+1]][kk]-'a']=1;
				in[s[a[i+1]][kk]-'a']++;
				break;
			}
			k++;kk++;
		}
		if(cnt==0&&s[a[i]].size()>s[a[i+1]].size()) {
			puts("NE");
			return 0;
		}
	}
	if(cnt==0) {
		puts("DA");
		for(int i=0;i<=25;i++) cout<<char(i+'a');
		return 0;
	}
	int t=topo();
	if(t==-1) {
		puts("NE");
		return 0;
	}
	sort(ans+1,ans+1+sum);
	for(int i=1;i<=sum;i++) {
		anss[ans2[i]]=ans[i];
	}
	for(int i=0;i<=25;i++) {
		if(vis[i]&&!viss[i]) {
			puts("NE");
			return 0;
		}
		if(!viss[i]) anss[i]=i;
	}
	puts("DA");
	for(int i=0;i<=25;i++) {
		cout<<char(anss[i]+'a');
	}
	return 0;
}
