        }
        for(i=1;i<n;i++){
            for(j=0;j<S[i].size();j++){
                if(j>=S[i+1].size()){
                    puts("NE");
                    return 0;//一个字符串是另一个字符串的子串，还要排在后面，显然无解
                }
                if(st[S[i][j]][S[i+1][j]]) break;//已经满足大小关系
                if(S[i][j]==S[i+1][j]) continue;//相同
