		}
	}ntt;
	void Inv(const int *a,int *ans,int n){
		if(n==1) return ans[0]=fpow(a[0],mod-2),ans[1]=0,void();
		static int res[maxn];
		Inv(a,ans,n>>1);
		int lim=n<<1;
