#include<cstdio>
using namespace std;
#define int long long
long long qread(){
    long long w=1,c,ret;
    while((c=getchar())> '9'||c< '0')
    w=(c=='-'?-1:1); ret=c-'0';
    while((c=getchar())>='0'&&c<='9')
    ret=ret*10+c-'0';
    return ret*w;
}
int n,k,len,x,ans,tot[257][9],z,X=(1<<8)-1;
signed main(){
	scanf("%lld%lld",&n,&k);
	for(int i=1;i<=n;i++){
		x=qread();
		for(int j=0;j<=7;j++){
			tot[x&X][j]++;
			x>>=8;
		}
	}
	for(int i=0;i<=7;i++)
	for(int j=1;j<256;j++)
	if(tot[j][i]%k)ans+=j<<(8*i);
	printf("%lld",ans);
	return 0;
