    cin >> n;
    for (int i = 0; i < n; i++) {
        cin >> s[i];
    }
    for (int i = 1; i < n; i++) {
        int a, b;
        a = s[0];
        b = s[i];
        int g = __gcd(a, b); //正规函数写法参考上面链接）
        a /= g;
        b /= g;
        cout << a << "/" << b << endl;
    }
    return 0;
