```c++
#include <bits/stdc++.h>
using namespace std;

string s;

signed main(){
	cin >> s;
	while(s.length()%3) s = '0'+s;
	for(int i = 0; i < s.length(); i ++){
		if(i%3 != 2) continue;
		for(int j = 2, num = 0; ~j; j --){
			num += (s[i-j]-'0') ? (int)pow(2,j) : 0;
			if(!j) printf("%d", num);
		}
	}
	return 0;
}
```

