	cin>>n;
	x=n.size();
	//求长和宽
	for(int i=1;i*i<=x;i++){
		if(x%i==0){
			a=i;
			b=x/i;
		}
	}
	//重点来了！
	for(int i=1;i<=a;i++){
		while(k[ks]){
			ks++;
		}
 		//防止一些bug
		cout<<n[ks];
		k[ks]=1;
		for(int j=1;j<b;j++){
			cout<<n[ks+j*a];//这就是我前面讲了大半天的东东
