#include <iostream>
using namespace std;
string s;
int l, h;
char ans[105][105];
int main() {
    cin >> s;
    int n = s.size(); //字符数量
    for (int i = 1; i * i <= n; i++) {
        if (n % i == 0) { //计算长宽，尽可能接近平方根为最大
            h = i;
            l = n / i;
        }
    }
    int cnt = 0;
