bool cmp(node x, node y) {
	if(x.f != y.f)
		return x.f > y.f;
	if(x.data != y.data)
		return x.data < y.data;
	if(!x.data) 
		return x.v < y.v;
	return x.v > y.v;
}

signed main() {
	memset(dp, -0x3f, sizeof(dp));
	dp[0] = 0;
	scanf("%lld", &n);
	for(int i = 1; i <= n; i++) {
		scanf("%lld %lld %lld", &a[i].c, &a[i].f, &a[i].v);
		a[i].data = 0;
	}
	scanf("%lld", &m);
	for(int i = n + 1; i <= n + m; i++) {
		scanf("%lld %lld %lld", &a[i].c, &a[i].f, &a[i].v);
		a[i].data = 1;
	}
	num = m + n;
	std :: stable_sort(a + 1, a + num + 1, cmp);
	/*
