	*/
	for(int i = 1; i <= num; i++) {
		if(!a[i].data) { // 计算机 
			C += a[i].c;
			for(int j = C; j >= a[i].c; j--)
				dp[j] = std :: max(dp[j], dp[j - a[i].c] - a[i].v);
		} else { // 客户 
			for(int j = 0; j <= C - a[i].c; j++) 
				dp[j] = std :: max(dp[j], dp[j + a[i].c] + a[i].v);
		}
