	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++)
	{
		scanf("%d%s",&a[i].score,a[i].temp);
	} 
	for(int i=1;i<=m;i++)
	{
		for(int j=1;j<=m;j++)
		{
			scanf("%lf",&f[i][j]);
