		double total=0;//总分 
		double avver;//平均分 
		for(int j=1;j<=k;j++){
			total+=a[j][i]*1.0;

		}
		avver=total/k*1.0;
//		printf("伪平均分:%lf\n",avver);//调试：输出伪平均分 
		score[i]=gp(avver,i);//将各个队伍的真实平均分算出 
	}
	for(int i=1;i<=n;i++){
		double sco=0;
		sco=score[stu[i].dui]*0.4+0.6*stu[i].s;//算出double类型的学生最终分数 
