#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<map>
using namespace std;
int temp,n;
long long ans;
int vis[10000010];
int main()
{
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		int a,b;
		scanf("%d%d",&a,&b);
		if(a!=(a^b))
		vis[a]++;
		vis[a^b]++;
