	{
		for(j=1;j<=Side;j++)
		{
			cin>>Map[i][j];
		}
	}
	for(i=1;i<=Side;i++)
	{
		for(j=1;j<=Side;j++)
		{
			if(Map[i][j]>='A'&&Map[i][j]<='Z')
			{
				if(Check(i,j))
				{
					cout<<Map[i][j]<<endl;
					return 0;
				}
			}
		}
	}
	cout<<"ongoing"<<endl;
