#define int long long//
using namespace std;
const int INF = 2e15;
const int mod = 1e9 + 7;
const int N = 3e4 + 10;
const int M = (1<<20) + 10;

inline int read(){
	int x=0,f=1;char ch=getchar();
	for(;!isdigit(ch);ch=getchar()) if(ch=='-') f=-1;
	for(; isdigit(ch);ch=getchar()) x=(x<<3)+(x<<1)+ch-'0';
	return x*f;
}
char mp[N][51];
int path[51][N],cnt[51],r,c;
void down(int x){
	while(true){
		int tmp = path[x][cnt[x]];
		if(mp[cnt[x]][tmp] == 'O') --cnt[x];
		else if(cnt[x] == r) break;
		else if(mp[cnt[x]+1][tmp] == 'X') break;
		else if(mp[cnt[x]+1][tmp] == '.') path[x][++cnt[x]] = tmp;
		else if(mp[cnt[x]+1][tmp-1] == '.' && mp[cnt[x]][tmp-1] == '.') path[x][++cnt[x]] = tmp - 1;
		else if(mp[cnt[x]+1][tmp+1] == '.' && mp[cnt[x]][tmp+1] == '.') path[x][++cnt[x]] = tmp + 1;
		else break;
	}
	mp[cnt[x]][path[x][cnt[x]]] = 'O';
}
signed main(){
	r = read() , c = read();
	for(int i=1;i<=r;i++){
		scanf("%s",mp[i]+1);
	}
	for(int i=1;i<=c;i++) path[i][0] = i;
	int n = read();
	for(int i=1;i<=n;i++){
		int x = read();
		down(x);
	}
	for(int i=1;i<=r;i++){
		printf("%s\n",mp[i]+1);
	}
	return 0;
}

