```
#include <bits/stdc++.h>
using namespace std;
int main() {
    int a,b,c,d,e,f,g,cnt=0;//ab表示一号狗发疯的时间和安静的时间cd表示二号狗发疯的时间和安静的时间，efg是三个人到达的时间
    cin>>a>>b>>c>>d>>e>>f>>g;//输入
    cnt=0;//计狗器（huaji
    if(e%(a+b)<=a&&e%(a+b)>0){//一号狗2的一个周期是（a+b），算算除以周期后余几就行了
    	cnt++;//统计加一
	}
	if(e%(c+d)<=c&&e%(c+d)>0){//统计二号狗
    	cnt++;//统计加一
	}
	if(cnt==0){//计数器是零
		cout<<"none"<<endl;//没狗
	}//
	if(cnt==1){//计数器是一
		cout<<"one"<<endl;//一只狗
	}
	if(cnt==2){//计数器是二
		cout<<"both"<<endl;//两只狗
	}
	cnt=0;//归零
	if(f%(a+b)<=a&&f%(a+b)>0){//重复三个人的
    	cnt++;
	}
	if(f%(c+d)<=c&&f%(c+d)>0){
    	cnt++;
	}
	if(cnt==0){
		cout<<"none"<<endl;
	}
	if(cnt==1){
		cout<<"one"<<endl;
	}
	if(cnt==2){
		cout<<"both"<<endl;
	}
	cnt=0;
	if(g%(a+b)<=a&&g%(a+b)>0){
    	cnt++;
	}
	if(g%(c+d)<=c&&g%(c+d)>0){
    	cnt++;
	}
	if(cnt==0){
		cout<<"none"<<endl;
	}
	if(cnt==1){
		cout<<"one"<<endl;
	}
	if(cnt==2){
		cout<<"both"<<endl;
	}
	cnt=0;
    return 0;
}
