	return x.val<y.val;

}

int main(){

	scanf("%d%d",n,k);

	for(register int i=0;i<n;i++){
		scanf("%d",v[i]);

		a[i].val=v[i];

		a[i].num=i;
	}

	sort(a,a+n,cmp);

	for(register int i=0;i<n;i++){	
		v[a[i].num]=i/k;
	} 

	for(register int i=0;i<n;i++){
		answer[i]=1;

		for(register int j=0;j<i;j++){
			if(v[j]<=v[i]) answer[i]=max(answer[i],answer[j]+1);
		}

		cnt=max(cnt,answer[i]);
	}



	printf("%d\n",n-cnt);

	return 0;
} 
