#include<climits>
#define re register
#define in inline
#define pi acos(-1.0)
#define inf 2147483640
#define cd complex<double>
using namespace std;
in int read()
{
    int sum=0,negative=1;
    char ch=getchar();
    while(ch<'0'||ch>'9')
    {
        if(ch=='-')negative=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9')
    {
        sum=sum*10+ch-'0';
        ch=getchar();
    }
    return sum*negative;
}
in void write(int X)
{
    if(X<0)putchar('-'),X=-X;
    if(X>=10)write(X/10);
    putchar(X%10+'0');
}
int main()
{
	char last='\0',now;
	while(now!='\n'){
		now=getchar();
		if(now=='p'){
			if(last=='a'||last=='e'||last=='i'||last=='o'||last=='u'){
				getchar();
				now=getchar();
			}
		}
		putchar(now);
		last=now;
	}
    return 0;
}
```

