#include<bits/stdc++.h>
using namespace std;
const int TT=10000;
const int maxn=10005;
int n;
struct Big_int{
	int len,a[maxn];
	Big_int(){len=0;memset(a,0,sizeof a);}//清空
	Big_int(int x){
		len=0;memset(a,0,sizeof a);
		while (x) a[++len]=x%TT,x/=TT;
	}//按位脱离
	Big_int operator +(const Big_int b){
		Big_int c;
		c.len=max(len,b.len);
		for (int i=1;i<=c.len;i++){
			c.a[i]+=a[i]+b.a[i];
			c.a[i+1]+=c.a[i]/TT;//进位
			c.a[i]%=TT;
		}
		if (c.a[c.len+1]) c.len++;//如果最后还有，那就再进一位
		return c;
	}//高精度+
	Big_int operator *(const Big_int b){
		Big_int c;
		c.len=len+b.len-1;
		for (int i=1;i<=len;i++)
		for (int j=1;j<=b.len;j++){
			c.a[i+j-1]+=a[i]*b.a[j];
			c.a[i+j]+=c.a[i+j-1]/TT;//进位
			c.a[i+j-1]%=TT;
		}
		if (c.a[c.len+1]) c.len++;
		while (c.len>1&&!c.a[c.len]) c.len--;//如果有一堆前导0，干掉
		return c;
	}高精度*
	void print(){
		printf("%d",a[len]);
		for (int i=len-1;i;i--) printf("%04d",a[i]);
		printf("\n");
	}//输出，此处要注意的是四位一起处理的
}x,ans;//高精度
char a[maxn];
int main(){
	scanf("%s",a+1);n=strlen(a+1);
	x=1;ans=1;
	for(int i=1;i<=n;i++){
		if(a[i]=='L')ans=ans*2;//往左走，值乘以2，其余不变
		if(a[i]=='R')ans=ans*2+x;//往右走，值乘以2，由于之前有多种可能，叶子节点的可能数变多，不能只加一次1，是3^星号个数
		else if(a[i]=='*'){
			ans=ans*5;ans=ans+x;
        //ans=ans(不动)+2*ans(往左走)+2*ans+x(往右走)
        x=x*3;//又多了三种可能，乘以之前的可能即为最多可能
		}
	}
	ans.print();//高精度输出
	return 0;
}
