	ifav[N]=ksm(fav[N],Mod-2);
	for (int i=N-1;~i;i--) ifav[i]=ifav[i+1]*(i+1)%Mod; 
}
int C(int x,int y) {
	if (x<y) return 0;
	return fav[x]*ifav[y]%Mod*ifav[x-y]%Mod; 
}
signed main()
{
	ios::sync_with_stdio(false);
	cin>>n>>a>>b>>c>>Mod;
	if (n==1) {
		cout<<1%Mod<<"\n";
