//		sum%=Mod;
//	}
	// S=1 + (a+b)^1+ (a+b)^2+ (a+b)^3 + ...
	// (a+b)*S=(a+b)+(a+b)^2+ (a+b)^3+ (a+b)^4 + ...
	// (a+b-1)*S=(a+b)^{n-1}-1
	sum=ksm(a+b,n-1)-1;

	sum%=Mod;sum*=ksm(a+b-1,Mod-2);
	sum%=Mod;sum*=c;sum%=Mod;
	int sum1=0;
//	for (int j=0;j<n;j++) {
