	    }
	}
	if(n==45){ // 45 度的情况
		for(int i=1;i<=a+b-1;i++){
			for(int j=1;j<=abs(a-i);j++)cout<<" ";
			for(int j=a;j>=1;j--){
			    for(int k=b;k>=1;k--)
			    if(j+k==i+1)cout<<c[j][k]<<" ";
			}
			cout<<endl;
		}
	}
	if(n==90){ // 90 度的情况
		for(int i=1;i<=b;i++){
			for(int j=a;j>=1;j--){
				cout<<c[j][i];
			}
			cout<<endl;
		}
	}
	if(n==135){ // 135 度的情况
		for(int i=1;i<=a+b-1;i++){
			for(int j=1;j<=abs(b-i);j++)cout<<" ";
			for(int j=a;j>=1;j--){
				for(int k=1;k<=b;k++)
			    if(a+k==i+j)cout<<c[j][k]<<" ";
			}
			cout<<endl;
		}
	}
	if(n==180){ // 180 度的情况
		for(int i=a;i>=1;i--){
			for(int j=b;j>=1;j--){
				cout<<c[i][j];
			}
			cout<<endl; 
		}
	}
	if(n==225){ // 225 度的情况
		for(int i=1;i<=a+b-1;i++){
			for(int j=1;j<=abs(a-i);j++)cout<<" ";
			for(int j=1;j<=a;j++){
		        for(int k=1;k<=b;k++)
			    if(a+b+1==i+j+k)cout<<c[j][k]<<" ";
			}
			cout<<endl;
		}
	}
	if(n==270){ // 270 度的情况
		for(int i=b;i>=1;i--){
			for(int j=1;j<=a;j++){
				cout<<c[j][i];
			}
			cout<<endl;
		}
	}
	if(n==315){ // 315 度的情况
		for(int i=1;i<=a+b-1;i++){
			for(int j=1;j<=abs(b-i);j++)cout<<" ";
			for(int j=1;j<=a;j++){
				for(int k=b;k>=1;k--)
			    if(j+b-k==i)cout<<c[j][k]<<" ";
			}
			cout<<endl;
		}
	}
	return 0; //完美结束
}
