namespace fastIO{
	inline int read(){
		int x = 0, s = 1;
		char c = getchar();
		while(c < '0' || c > '9'){
			if(c == '-') s = -1;
			c = getchar();
		}
		while(c >= '0' && c <= '9'){
			x = (x << 3) + (x << 1) + (c ^ '0');
			c = getchar();
		}
		return x * s;
	}
	inline void Write(int x){
		if(x < 0){
			putchar('-');
			x = -x;
		}
		if(x > 9) Write(x / 10);
		putchar(x % 10 + '0');
	}
	inline void write(int x, char c){
		Write(x);
		putchar(c);
	}
}
using namespace fastIO;
namespace SyxQwQ{
	inline int qwq(){
		return 0;
	}
	int a[114514];
	inline int main(){
		//todo
		int n = read(), xtp = read(), ans = 0;
		f(i, 1, n, 1) a[i] = read();
		if(xtp == 1){
			f(i, 4, n, 1) if(a[i] == a[i-1] && a[i] == a[i-2] && a[i] == a[i-3]) ans++;
			ans += n;
		}else if(xtp == 2){
			f(i, 2, n, 1) if(a[i] == a[i-1]) ans++;
		}else if(xtp == 3){
			f(i, 3, n, 1){
				if(a[i-1] == a[i-2] && a[i-1] + 1 == a[i]) ans++;
				else if(a[i-1] - 1 == a[i]) ans++;
			}
		}else if(xtp == 4){
			f(i, 3, n, 1){
				if(a[i] == a[i-1] && a[i-2] - 1 == a[i-1]) ans++;
				else if(a[i-1] + 1 == a[i]) ans++;
			}
		}else if(xtp == 5){
			f(i, 3, n, 1) if(a[i] == a[i-1] && a[i] == a[i-2]) ans++;
			f(i, 3, n, 1) if(a[i] == a[i-2] && a[i] == a[i-1] + 1) ans++;
			f(i, 2, n, 1){
				if(a[i-1] + 1 == a[i]) ans++;
				else if(a[i-1] - 1 == a[i]) ans++;
			}
		}else if(xtp == 6){
			f(i, 3, n, 1) if(a[i] == a[i-1] && a[i] == a[i-2]) ans++;
			f(i, 3, n, 1) if(a[i] == a[i-1] && a[i] == a[i-2] + 1) ans++;
			f(i, 2, n, 1) if(a[i] == a[i-1]) ans++;
			f(i, 2, n, 1) if(a[i-1] - 2 == a[i]) ans++;
		}else if(xtp == 7){
			f(i, 3, n, 1) if(a[i] == a[i-1] && a[i] == a[i-2]) ans++;
			f(i, 3, n, 1) if(a[i-2] == a[i-1] && a[i] == a[i-1] - 1) ans++;
			f(i, 2, n, 1) if(a[i] == a[i-1]) ans++;
			f(i, 2, n, 1) if(a[i-1] + 2 == a[i]) ans++;
		}
		write(ans, '\n');
		return qwq();
	}
}
signed main(){
	SyxQwQ::main();
	return 0;
}
